class Solution {
public:
    const int MOD = 1000000007;
    int add(int x, int y) {
        return (x + y) % MOD;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int dp[n][1005];
        memset(dp, 0, sizeof dp);
        for(int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = i + 1;
        }
        dp[0][1000] = nums[0] + 1;
        for(int i = 1; i < n; ++i) {
            int cur = nums[i];
            for(int j = 0; j <= cur; ++j) {
                int k = cur - j;
                int prev = nums[i - 1];
                //<= j and >= k
                int lb = min(j, prev - k);
                if(lb >= 0) dp[i][j] = dp[i - 1][lb];
            }
            for(int j = 1; j <= 1000; ++j) {
                dp[i][j] = add(dp[i][j], dp[i][j - 1]);
            }
        }
        return dp[n - 1][1000];
    }
};
