#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int M=1e9+7, n=nums.size();
        vector<vector<ll>> dp(n, vector<ll>(1e3+1, 0));
        for(int i=0;i<=nums[0];i++) dp[0][i]=1;
        for(int i=1;i<=1e3;i++) dp[0][i]+=dp[0][i-1];
        for(int i=1;i<n;i++) {
            for(int x=0;x<=nums[i];x++) {
                int pos=min(x, x+nums[i-1]-nums[i]);
                if(pos>=0) dp[i][x]=dp[i-1][pos];
            }
            for(int x=1;x<=1e3;x++) dp[i][x]=(dp[i][x]+dp[i][x-1])%M;
        }
        return dp[n-1].back();
    }
};
