class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int md = 1e9 + 7;
        vector<vector<long long>> f(nums.size(), vector<long long>(1e3 + 1));
        vector<vector<long long>> g(nums.size(), vector<long long>(1e3 + 1));
        for(int i = 0; i <= nums[0]; ++i) f[0][i] = 1;
        g[0][0] = 1;
        for(int i = 1; i <= 1e3; ++i) g[0][i] = g[0][i - 1] + f[0][i];
        for(int i = 1; i < nums.size(); ++i) {
            for(int j = 0; j <= nums[i]; ++j) {
                // nums[i - 1] - nums[i] + j >= k
                int k = min(j, nums[i - 1] - nums[i] + j);
                if (k < 0) continue;
                f[i][j] = (f[i][j] + g[i - 1][k]) % md;
            }

            g[i][0] = f[i][0];
            for(int j = 1; j <= 1e3; ++j) g[i][j] = (g[i][j - 1] + f[i][j]) % md;


        }

        long long ret = 0;
        for(int i = 0 ; i <= nums.back(); ++i) ret = (ret + f.back()[i]) % md;
        return ret;
    }
};
