class Solution {
public:
    const int mod = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>>dp(n,vector<int>(1001,0));
        for(int i = 0;i <= nums[0];i++) dp[0][i] = i + 1;
        for(int i = nums[0] + 1;i <= 1000;i++) dp[0][i] = dp[0][i - 1];
        int ans = 0;
        for(int i = 1;i < n;i++) {
            int s = 0;
            for(int j = 0;j <= 1000;j++) {
                if(j <= nums[i] && nums[i - 1] - nums[i] + j >= 0)
                    dp[i][j] = dp[i - 1][min(j,nums[i - 1] - nums[i] + j)];
                dp[i][j] %= mod;
                if(j) dp[i][j] += dp[i][j - 1];
                dp[i][j] %= mod;
            }
        }
        return dp[n-1][1000];
    }
};
