class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const long long MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<long long>> dp(n+5, vector<long long>(1005, 0));
        vector<vector<long long>> pref(n+5, vector<long long>(1005, 0));
        long long ans = 0;
        for(int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
            pref[0][i] = ((i-1 >= 0 ? pref[0][i-1] : 0) + dp[0][i]) % MOD;
        }
        for(int i = 1; i < n; i++) {
            for(int j = 0, k; j <= nums[i]; j++) {
                k = min(j, nums[i-1] - nums[i] + j);
                if(k >= 0) {
                    dp[i][j] = pref[i-1][k];
                }
                // for(int k = 0; k <= nums[i-1]; k++) {
                //     if(k <= j && nums[i-1] - nums[i] + j >= k) {
                //         (dp[i][j] += dp[i-1][k]) %= MOD;
                //     }
                // }
                pref[i][j] = ((j-1 >= 0 ? pref[i][j-1] : 0) + dp[i][j]) % MOD;
            }
        }
        for(int i = 0; i <= nums.back(); i++) {
            (ans += dp[n-1][i]) %= MOD;
        }
        return ans;
    }
};
