typedef long long int ll;
typedef long double ld;

#define MOD (ll) 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> val, sv(nums[0]+1,1);
		for(int i=1;i<=nums[0];i++){sv[i] = sv[i-1] + 1;}
		for(int i=1;i<nums.size();i++){
			val.clear();
			val.resize(nums[i]+1,0);
			for(int j=0;j<=nums[i];j++){
				int k = nums[i-1]-(nums[i]-j);
				if(k >= 0){val[j] += sv[min(j,k)];}
			}
			sv.clear();
			sv.push_back(val[0]);
			for(int j=1;j<=nums[i];j++){
				sv.push_back(val[j] + sv[j-1]);
			}
			for(auto &x : sv){x = (x%MOD);}
		}
		return sv.back();
    }
};
