using ll = long long;

class Solution {
 public:
  int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    vector<ll> dp(1001, 0);
    for (int i = 0; i <= nums[0]; ++i) {
      dp[i] = 1;
    }
    for (int t = 1; t < n; ++t) {
      vector<ll> new_dp(1001, 0);
      int j = 1000;
      ll sum = 0;
      for (int i = nums[t]; i >= 0; --i) {
        while (j >= i && nums[t] - i >= nums[t - 1] - j) {
          sum += dp[j];
          --j;
        }
        new_dp[i] = sum;
      }
      swap(dp, new_dp);
      for (auto& c : dp) {
        c %= MOD;
      }
    }
    ll ret = 0;
    for (auto c : dp) {
      ret += c;
    }
    ret %= MOD;
    return ret;
  }

  static constexpr ll MOD = 1000000007;
};
