class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<ll> ps(a[0] + 1);
        for (int x = 0; x <= a[0]; ++x) {
            ps[x] = x + 1;
        }

        // ll new_[1001] = {0};
        for (int i = 1; i < n; ++i) {
            vector<ll> new_(a[i] + 1, 0);
            // mst(new_, 0);
            for (int j = 0; j <= a[i]; ++j) {
                int k = min(j, a[i-1] - (a[i] - j));
                int v = 0;
                if (k >= 0) {
                    v = ps[k];
                }
                new_[j] = v;
                if (j <= 0) {
                    continue;
                }
                new_[j] = (v + new_[j-1]) % mod;
            }
            ps = new_;
        }
        return ps.back();

    }
};
