#include <bits/stdc++.h>
using namespace std;

#define ll long long int
#define INF 1000000000000000 //1e17
const ll mod = 1000000007;
#define N 1001 

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n =nums.size() ;

        ll dp[n][N] ;
        memset(dp,0,sizeof(dp)) ;

        ll prefix[N] ;
        memset(prefix,0,sizeof(prefix)) ;

        //B 
        for(ll i =0 ; i<=nums[0] ;i++ ){
            dp[0][i] =1 ;
            prefix[i] = dp[0][i] ;
        }
        for(ll i=1 ; i<N ;i++ ){
            prefix[i]+=prefix[i-1] ;
        }

        //T
        for(ll i =1 ; i<n ;i++ ){
            ll newPrefix[N] ;

            for(ll j =0 ; j<N ;j++ ){
                ll k = nums[i]- j ;
                if(k<0)break ;

                ll L = 0 , R = min(j , nums[i-1] -k ) ;

                if(L<=R){
                    ll p = prefix[R] - ( L>0 ? prefix[L-1] : 0 ) ;
                    dp[i][j]+=( p ) ;
                    dp[i][j]%=mod ;
                }
                // for(ll prev = 0 ; prev <=j ;prev++ ){
                //     ll pprev = nums[i-1] -prev ;
                //     if( pprev >= k){
                //         dp[i][j]+=dp[i-1][prev] ;
                //         dp[i][j]%=mod ;
                //     }
                // }
            }

            for(ll j =0 ; j<N ;j++ ){ 
                prefix[j] = dp[i][j] ;
                if(j>0)prefix[j]+=prefix[j-1] ;
                prefix[j]%=mod ;
            }

        }

        ll res =0 ;
        for(ll j=0 ; j<N ;j++ ){
            res+=dp[n-1][j] ;
            res%=mod ;
        }
        return res ; 
    }
};
