#define imie(...) " [" << #__VA_ARGS__ ": " << (__VA_ARGS__) << "] "



const int N = 1005;
const int mod = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<int>> dp(n, vector<int>(N));
        vector<int> pre(N);

        for(int x = 0; x <= a[0]; x++){
            dp[0][x] = 1;
            pre[x] = 1;
        }
        for(int x = 1; x < N; x++) pre[x] = (pre[x] + pre[x-1]) % mod;

        for(int i = 1; i < n; i++){
            int cur = a[i];
            int last = a[i-1];
            vector<int> npre(N);
            for(int x = 0; x <= cur; x++){
                int y = min(last + x - cur, x);
                if(y >= 0){
                    dp[i][x] = (dp[i][x] + pre[y]) % mod;
                    npre[x] = (npre[x] + dp[i][x]) % mod;
                }
            }
            for(int x = 1; x < N; x++) npre[x] = (npre[x] + npre[x-1]) % mod;
            swap(pre, npre);
            // debug() << imie(dp[i]);
        }

        int res = 0;
        for(int i = 0; i < N; i++){
            res = (res + dp[n-1][i]) % mod;
        }
        return res;
    }
};
