class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int mod = 1e9 + 7;
        int n = nums.size();

        const int M = 1005;
        vector<int> dp(M);
        for (int i = 0; i <= nums[0]; ++i) dp[i] = 1;
        for (int i = 1; i < M; ++i) dp[i] = (dp[i-1] + dp[i]) % mod;

        for (int i = 1; i < n; ++i) {
            for (int j = M-1; j >= 0; --j) {
                if (j > nums[i]) dp[j] = 0;
                else {
                    int till = min(j, nums[i-1] - nums[i] + j);
                    if (till < 0) dp[j] = 0;
                    else dp[j] = dp[till];
                }
            }
            for (int j = 1; j < M; ++j) dp[j] = (dp[j] + dp[j-1]) % mod;
        }
        return dp[M-1];
    }
};
