class Solution {
public:
    const int mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n+1, vector<int>(1001));
        long long pre[n+1][1001];
        for(int j = 0;j<=nums[n-1];j++){
            dp[n-1][j] = 1;
        }

        pre[n-1][0]  = dp[n-1][0];
        for(int j = 1;j<=1000;j++){
            pre[n-1][j] = (pre[n-1][j-1] + dp[n-1][j])%mod;
        }

        for(int i = n-2;i>=0;i--){
            int x = nums[i+1];
            for(int j = 0;j<=min(x, nums[i]);j++){
                int bj = nums[i] - j;

                long long r = x;
                long long l = max(x - bj,j);

                long long res = ((pre[i+1][r] - (l?pre[i+1][l-1]:0))%mod  + mod)%mod;
                // for(int k = j; k<= x;k++) res += dp[i+1][k];


                dp[i][j] = res;
            }

            pre[i][0] = dp[i][0];
            for(int j = 1;j<=1000;j++) pre[i][j] = (pre[i][j-1] + dp[i][j])%mod;
        }

        // for(int i = n-1;i>=0;i--){
        //     for(int j = 0;j<=nums[i];j++){
        //         cout<<i<<" "<<j<<" -> "<<dp[i][j]<<endl;
        //     }
        // }

        long long ans = 0;
        for(int i= 0;i<=nums[0];i++) ans = (ans + dp[0][i])%mod;
        return ans;
    }
};
