const long long INF = 1000;
const long long mod = 1e9 + 7;
typedef long long ll;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int  n =nums.size();
        int mx = *max_element(nums.begin(),nums.end());
        vector<vector<long long>> dp(n+2,vector<long long>(mx+2,0));
        dp[0][0] = 1;
        for(int i=1;i<=n;i++) {
            int a=  nums[i-1];
            for(int j=0;j<=mx;j++) {
                dp[i][j] = (j ? dp[i][j-1] : 0); 
                if(j>a) continue;
                int cc;
                if(i==1) cc = 0;
                else {
                    int aa = nums[i-2];
                    cc = min(j-a+aa,j);

                }
                // [0,j] & [0,j-a-a[i-1]]
                if(cc>=0) dp[i][j] += dp[i-1][cc];
                dp[i][j] %=mod;
                // cout << i << " " << j << " " << cc << " " << dp[i][j] << endl;
            }
        }
        return (int)dp[n][mx];

    }
};
