class Solution {
public:
    int countOfPairs(vector<int>& arr) {
        int mod = 1000000007;
        int n = arr.size();
        int dp[2][1002];
        for(int i=0;i<=1001;i++)dp[1][i]=0,dp[0][i]=0;
        for(int i=0;i<=arr[0];i++)dp[0][i]=1;
        for(int i=1;i<=1000;i++)dp[0][i]+=dp[0][i-1];
        for(int i=1;i<n;i++){
            for(int j=0;j<=arr[i];j++){
                int ind = min(arr[i-1]-arr[i]+j,j);
                // cout<<i <<" " << j << ind <<" " << endl;
                if(ind<0)continue;
                dp[i%2][j] = dp[1-i%2][ind];
            }
            for(int j=1;j<=1000;j++){
                dp[i%2][j]+=dp[i%2][j-1];
                dp[i%2][j]%=mod;
                dp[1-i%2][j]=0;
            }
            dp[1-i%2][0]=0;
        }
        return dp[(n-1)%2][1000];

    }
};
