class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int N=1001;
        const int mod=1e9+7;
        nums.insert(nums.begin(),N);
        int n=nums.size();
        vector<long long> dp(N,0);
        for(int i=0;i<N;i++) dp[i]=1;
        for(int i=1;i<n;i++){
            vector<long long> temp(N,0);
            for(int curr=0;curr<=nums[i];curr++){
                int mn=min({nums[i-1],curr,nums[i-1]-nums[i]+curr}); 
                if(mn>=0 && mn<=1000)temp[curr]=dp[mn];
            }
            dp=temp;
            for(int i=1;i<N;i++) dp[i]=(dp[i]+dp[i-1])%mod;
        }
        // for(int i=0;i<N;i++) ans=(ans+dp[i])%mod;
        return dp[N-1];
    }
};
