class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        x = nums[0]
        dp = [1] * (x + 1)
        for x in nums[1:]:
            acc = list(accumulate(dp))
            acc.append(0)
            dp = []
            for i in range(x + 1):
                # arr2_val = x - i
                # len(acc) - 1 - low_limit >= arr2_val
                # low_limit <= len(acc) - 1 - arr2_val
                limit = min(i, len(acc) - 2 - x + i)
                if limit >= 0:
                    dp.append(acc[limit] % mod)
                else:
                    dp.append(0)
        return sum(dp) % mod
