class Solution {
    long[][] dp;// = new long[1001][2001];
    long[][] pr;// = new long[1001][2001];
    int n;
    int mx;
    long mod = (long) (1e9 + 7);

    public int countOfPairs(int[] nums) {
        n = nums.length;
        for (int i = 1; i <= n; i++) {
            mx = Math.max(mx, nums[i-1]);
        }


        dp = new long[mx+1][n+1];
        pr = new long[mx+1][n+1];

        for (int i = 1; i <= n; i++) {
            if (i == 1) {
                for (int j = 0; j <= nums[0]; j++) {
                    dp[j][i] = 1;
                    pr[j][i] = dp[j][i];

                    if (j-1>=0) {
                        pr[j][i] += pr[j-1][i];
                        pr[j][i] %= mod;
                    }
                }
            } else {
                for (int j = 0; j <= nums[i-1]; j++) {
                    // (j, A-j)
                    // (0, B), (1, B-1) (2, B-2) ... (i, B-i) 
                    // i <= j
                    // B-i >= A-j
                    // i <= B-A+j

                    int mx = Math.min(Math.min(nums[i-2], j), nums[i-2]-nums[i-1] + j );
                    if (mx >= 0) dp[j][i] = pr[mx][i-1];
                    pr[j][i] = dp[j][i];
                    if (j-1>=0) {
                        pr[j][i] += pr[j-1][i];
                        pr[j][i] %= mod;
                    }
                }
            }
        }


        long answer = 0;

        for (int i = 0; i <= nums[n-1]; i++) {
            answer += dp[i][n];
            answer %= mod;
        }

        // print();


        return (int) answer;
    }

    void print() {
        for (int j = 0; j <= mx; j++) {
            for (int i = 1; i <= n; i++) {
                System.out.print(dp[j][i] + " ");
            }
            System.out.println();
        }

        System.out.println("\n\n");

        for (int j = 0; j <= mx; j++) {
            for (int i = 1; i <= n; i++) {
                System.out.print(pr[j][i] + " ");
            }
            System.out.println();
        }
    }

}
