const int INF = 0x3f3f3f3f;
const ll LLINF = 0x3f3f3f3f3f3f3f3f;
const ll MOD = 1e9+7; //998244353;
//===========================================
template<int MOD> struct mint {
    static const int mod = MOD;
     int v;
     explicit operator int() const { return v; }
    mint():v(0) {}
    mint(ll _v):v(int(_v%MOD)) { v += (v<0)*MOD; }
    mint& operator+=(mint o) {
        if ((v += o.v) >= MOD) v -= MOD;
        return *this; }
    mint& operator-=(mint o) {
        if ((v -= o.v) < 0) v += MOD;
        return *this; }
    mint& operator*=(mint o) {
        v = int((ll)v*o.v%MOD); return *this; }
    mint& operator/=(const mint& o) { return (*this) *= inv(o); }
    friend mint pow(mint a, ll p) { assert(p >= 0);
        return p==0?1:pow(a*a,p/2)*(p&1?a:1); }
    friend mint inv(mint a) { assert(a.v != 0); return pow(a,MOD-2); }
    friend mint operator+(mint a, mint b) { return a += b; }
    friend mint operator-(mint a, mint b) { return a -= b; }
    friend mint operator*(mint a, mint b) { return a *= b; }
    friend mint operator/(mint a, const mint& b) { return a /= b; }
};
using mi = mint<MOD>;
//===========================================
const int MAX = 1005;

class Solution {
public:
    int countOfPairs(vector<int>& v) {
        int n = siz(v);
        vector<vector<mi>> dp(n+2, vector<mi>(MAX, 0));
        for (int i = 0; i < MAX; i++) dp[0][i] = 1;
        for (int i = 1; i <= n; i++){
            int x = v[i-1], y = (i == 1? INF : v[i-2]);
            for (int j = 0; j <= x; j++){
                int lim = min(j, y-x+j);
                if (lim >= 0) dp[i][j] = dp[i-1][lim];
                //deb(i, j, (int)dp[i][j]);
                if (j) dp[i][j] += dp[i][j-1];
            }
        }
        return (int)dp[n][v[n-1]];
    }
};
