class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(), m=1e9+7;
        vector<array<int,2001>> dp(n+1);

        for (int i=0; i<=nums[0]; ++i) {
            dp[1][i]=1;
        }

        for (int i=1; i<=2000; ++i) {
            dp[1][i] += dp[1][i-1];
        }

        for (int i=1; i<n; ++i) {
            for (int j=0; j<=nums[i]; ++j) {
                int temp=nums[i]-j;
                int l=0, r=2000;
                while (l<=r) {
                    int mid=((l+r)>>1);
                    if (nums[i-1]-mid >= temp) l=mid+1;
                    else r=mid-1;
                }
                r=min(r, j);
                if (r>=0)
                    dp[i+1][j] = dp[i][r];

                // cout<<l<<" ";
                // for (int k=0; k<=j; ++k) {
                //     if (i==0 || (nums[i-1]-k >= nums[i]-j)) {
                //         dp[i+1][j] += dp[i][k];
                //         dp[i+1][j] %= m;
                //     }
                // }
            }
            for (int j=1; j<=2000; ++j) {
                dp[i+1][j] += dp[i+1][j-1];
                dp[i+1][j] %= m;
            }
        }

        // for (int i=0; i<n; ++i) {
        //     int temp=0;
        //     for (int j=0; j<=nums[i]; ++j) {
        //         cout<<dp[i+1][j]<<" ";
        //     }
        //     cout<<endl;
        // }

        return dp[n].back();

    }
};
