const int MOD = 1e9 + 7;
typedef long long ll;
const int UB = 2001;
class Solution {
public:

    int countOfPairs(vector<int>& a) {
        int n = a.size();
        ll dp[n][UB];
        ll sum[UB];

        for(int i=0; i<n; i++){
            for(int j=0; j<UB; j++){
                dp[i][j] = 0;
            }
        }

        for(int i=0; i<n; i++){
            for(int j=0; j<=a[i]; j++){
                int k = a[i] - j;
                if(i == 0){
                    dp[i][j] = 1;
                }
                else{
                    // int l = max(0, a[i] - j);
                    int l = 0;
                    int r = min({j, a[i - 1], a[i - 1] - a[i] + j});
                    if(l <= r)
                    dp[i][j] = (sum[r] - (l ? sum[l - 1] : 0) + MOD) % MOD;
                }
            }
            for(int j=0; j<UB; j++){
                sum[j] = dp[i][j];
                if(j)   sum[j] = (sum[j - 1] + sum[j]) % MOD;
            }
        }

        ll ans = 0;
        for(int j=0; j<=a.back(); j++){
            ans = (ans + dp[n - 1][j]) % MOD;
        }

        return ans;
    }
};
