class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mod = 1e9  + 7;
        int x = 1000;
        int n = nums.size();
        vector<vector<long long>> dp(n + 1, vector<long long>(x + 2, 0));

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        for(int j=x;j>=0;j--){
            dp[0][j] += dp[0][j+1];
            dp[0][j] %= mod;
        }

        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int idx = (j + max(0 , nums[i-1] - nums[i]));
                dp[i][j] = dp[i-1][idx];
            }
            for(int j=x;j>=0;j--){
                dp[i][j] += dp[i][j+1];
                dp[i][j] %= mod;
            }
        }

        long int ans = 0;

        ans = dp[n-1][0];
        return ans;
    }
};
