class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        r = nums[-1]
        for i in range(1,n):
            r -= max(0, nums[i] - nums[i-1])
        def ncr(n, r):
            grid = [[1]*(n+1) for i in range(n+1)]
            for i in range(2,n+1):
                for j in range(1, i):
                    grid[i][j] = (grid[i-1][j] + grid[i-1][j-1])%(10**9 + 7)
            return grid[n][r]
        if r < 0:
            return 0
        return ncr(n+r, r)
