class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7

        dp = [1] * (nums[0] + 1)
        for i in range(1, len(nums)):
            new_dp = [0] * (nums[i] + 1)
            diff = max(nums[i] - nums[i - 1], 0)
            count = 0
            for j in range(diff, nums[i] + 1):
                count = (count + dp[j - diff]) % mod
                new_dp[j] = count
            dp = new_dp

        return sum(dp) % mod
