class Solution {
public:
    long long cnt[2005][1005],pre[2005][1005];
    long long ans;
    const int MOD=(int)1e9+7;
    int countOfPairs(vector<int>& nums) {
        for(int i=nums.size()-1;i>=0;i--){
            for(int j=nums[i];j>=0;j--){
                if(i==nums.size()-1){
                    cnt[i][j]=1;
                    pre[i][j]+=pre[i][j+1]+cnt[i][j];
                }
                else {
                    int l=j,r=nums[i]-j;
                    int id=max(nums[i+1]-r,j);
                    cnt[i][j]=pre[i+1][id];
                    cnt[i][j]%=MOD;
                    pre[i][j]+=pre[i][j+1]+cnt[i][j];
                    pre[i][j]%=MOD;
                }
                if(i==0){
                    ans+=cnt[i][j];
                    ans%=MOD;
                }
            }
        }
        return ans;
    }
};
