class Solution {
public:

    #define ll long long
    #define MOD ((ll) 1e9+7)

    ll dp[2001][1006];

    int countOfPairs(vector<int>& nums) {

        memset(dp, 0, sizeof(dp));

        for(int i=0; i<=nums[0]; ++i) 
            dp[0][i] = i+1;

        for(int i=1; i<nums.size(); ++i) 
            for(int j=0; j<=nums[i]; ++j) {

                if(j) dp[i][j] = dp[i][j-1];

                int a = j, b = nums[i] - j;

                int l = 0, r = nums[i-1];

                while(l <= r) {

                    int m = (l + r) / 2;

                    if(m < a || nums[i-1]-m > b)
                        l = m+1;
                    else
                        r = m-1;

                }

                if(l == 0) 
                    dp[i][j] += dp[i-1][nums[i-1]];
                else
                    dp[i][j] += dp[i-1][nums[i-1]] - dp[i-1][l-1];

                dp[i][j] = (dp[i][j] + MOD) % MOD;

            }

        return dp[nums.size()-1][nums.back()];


    }
};
