class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        // dp[i][j] = number of monotonic pairs for nums from 0 to i with arr1 ending with j or lower (arr2 ended with nums[i] - j)
        int MOD = 1e9 + 7;

        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(1001));

        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }

        int ans = 0;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int other = nums[i] - j;
                int k = min(j, nums[i-1] - other);

                if (k >= 0) {
                    dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                }
                if (j != 0) {
                    dp[i][j] = (dp[i][j] + dp[i][j-1]) % MOD;
                }

                // for (int k = 0; k <= min(j, nums[i-1] - other); k++) {
                //     dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                // }
            }
        }

        for (int j = 0; j <= 1000; j++) {
            ans = (ans + dp[n-1][j]) % MOD;
        }
        return ans;
    }
};
