mod=10**9+7
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        dp=[[0]*1001 for _ in range(n)]
        pref=[[0]*1001 for _ in range(n)]
        for i in range(nums[0]+1):
            dp[0][i]=1
            pref[0][i]=1+i
        for i in range(1,n):
            for j in range(nums[i]+1):
                if nums[i-1]-nums[i]+j<0:
                    pref[i][j]=pref[i][j-1]
                    continue
                dp[i][j]=pref[i-1][min(nums[i-1]-nums[i]+j,j,nums[i-1])]
                pref[i][j]=pref[i][j-1]+dp[i][j]
        return sum(dp[-1])%mod
