#define ll long long
#define MOD 1000000007LL
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int r = *max_element(nums.begin(),nums.end());
        vector<vector<ll>> dp(n,vector<ll>(r+1,0));
        for (int x = 0; x <= nums[0]; ++x) dp[0][x] = 1;
        for (ll i = 1; i < n; ++i) {
            vector<ll> pfx = {dp[i-1][0]};
            for (ll rv = 1; rv <= r; ++rv){
                pfx.push_back((pfx.back() + dp[i-1][rv]) % MOD);
            }

            for (ll rv = 0; rv <= nums[i]; ++rv) {
                ll ext = nums[i] - rv;
                if (ext >= 0) {
                    ll nv = min(rv, (ll)nums[i-1] - ext);
                    if (nv >= 0) {
                        dp[i][rv] = pfx[nv];
                    }
                }
            }
        }

        int out = 0;
        for(int rv = 0; rv <= nums[n-1]; ++ rv){
            out += dp[n-1][rv];
            out %= MOD;
        }
        return out;

    }
};
