#define rep(i, a) for (int i = 0; i < (int)(a); i++)
#define all(a) a.begin(), a.end()
#define rall(a) a.rbegin(), a.rend()
using ll = long long;
const int mod = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        auto a = nums;
        vector<int> dp(a[0] + 1, 1);
        rep(i, n - 1) {
            vector<int> p(a[i + 1] + 1);
            swap(p, dp);
            int l = -1, r = a[i + 1];
            while (r - l > 1) {
                int m = (l + r) / 2;
                int x = a[i], y = a[i + 1] - m;
                if (x >= y) r = m;
                else l = m;
            }
            cout << r << endl;
            int cur = p[0], cnt = 1;
            for (int k = r; k <= a[i + 1]; k++) {
                dp[k] += cur;
                dp[k] %= mod;
                cur += p[cnt];
                if (cnt < a[i]) cnt++;
                cur %= mod;
            }
        }
        int ans = 0;
        for (int i = 0; i <= a[n - 1]; i++) {
            ans += dp[i];
            ans %= mod;
        }
        return ans;
    }
};
