class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;
        vector<vector<int>> dp(n + 10, vector<int>(1010));
        vector<int> a(n + 10);
        for (int i = 1; i <= n; ++i) a[i] = nums[i - 1];
        vector<int> sum(1010);
        auto get = [&](int l, int r) {
            if (r < 0) return 0;
            return sum[r];
        };
        sum[0] = 1;
        for (int i = 1; i <= n; ++i) {
            for (int j = 0; j <= a[i]; ++j) {
                if (i == 1) {
                    dp[i][j] = 1;
                } else {
                    dp[i][j] = get(0, min(j, a[i - 1] - a[i] + j));
                }
            }
            for (int j = 0; j <= 1000; ++j) {
                sum[j] = dp[i][j];
                if (j) sum[j] += sum[j - 1];
                sum[j] %= mod;
            }
        }
        int ans = 0;
        for (int i = 0; i <= 1000; ++i) {
            ans += dp[n][i];
            ans %= mod;
        }
        return ans;
    }
};
