class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        P = 10 ** 9 + 7
        old = []
        for a in range(nums[-1] + 1):
            b = nums[-1] - a
            if b >= 0:
                old.append([a, b, 1])
        for i in range(len(nums)-2, -1, -1):
            new, j = [], len(old) - 1
            for a in range(nums[i], -1, -1):
                temp = 0
                b = nums[i] - a
                while j >= 0 and old[j][0] >= a and old[j][1] <= b:
                    temp += old[j][2]
                    j -= 1
                if new:
                    temp += new[-1][2]
                if temp > 0:
                    new.append([a, b, temp])
            old = new[::-1]
        ans = 0
        for _, _, v in old:
            ans += v
        return ans % P
