class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),mx = *max_element(nums.begin(),nums.end()),ans=0,mod=1e9+7;
        vector<vector<int>>dp(mx+1,vector<int>(n,0));
        dp[nums[n-1]][n-1]=1;
        for(int i=0;i<nums[n-1];i++) dp[i][n-1]+=1;
        for(int j=n-2;j>=0;j--){
            for(int i=nums[j];i>=0;i--){
                int k = nums[j+1]-nums[j]+i;
                dp[i][j] = dp[max(i,k)][j+1];
                if(i<mx) dp[i][j] = (dp[i][j] + dp[i+1][j])%mod;
            }
        }
        for(int i=0;i<=nums[0];i++) ans = (ans + dp[i][0])%mod;
        return ans;
    }
};
