int mod = 1e9 + 7;
int dp[2002][1001];
int pre[2002][1001];

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        nums.insert(nums.begin(), 1000);

        for(int i = n + 1; i >= 1; i--) {
            for(int j = 0; j <= nums[i - 1]; j++) {
                if(i == n + 1)
                    dp[i][j] = 1;
                else {
                    int k = nums[i - 1] - j;
                    long long ans = 0;
                    int t = max(j, nums[i] - k);
                    if(t <= nums[i]) {
                        ans = pre[i + 1][nums[i]];
                        if(t > 0)
                            ans = (ans - pre[i + 1][t - 1] + mod) % mod;
                    }

                    dp[i][j] = ans;
                }
            }

            pre[i][0] = dp[i][0];
            for(int j = 1; j <= nums[i - 1]; j++)
                pre[i][j] = (pre[i][j - 1] + dp[i][j]) % mod;
        }

        return dp[1][0];
    }
};
