class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        const int M = 1e9 + 7;
        vector<int> x(1001, 0), d(1001, 0);
        fill(x.begin(), x.begin() + a[0] + 1, 1);
        for (int i = 1; i < n; ++i) {
            fill(d.begin(), d.end(), 0);
            int p = 0;
            for (int s = 0; s <= a[i]; ++s) {
                int v = a[i] - s;
                int m = min(s, a[i-1] - v);
                if (m >= 0) {
                    p = (p + x[m]) % M;
                    d[s] = p;
                }
            }
            swap(x, d);
        }
        int r = 0;
        for (int e = 0; e <= a[n-1]; ++e) {
            r = (r + x[e]) % M;
        }
        return r;
    }
};
