class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        dp = [{} for _ in range(n)]

        for v in range(nums[0] + 1):
            dp[0][v] = 1

        for i in range(1, n):
            cumulative = 0
            k = 0
            for v in list(range(nums[i] + 1)):
                while k <= nums[i-1] and k <= v and (nums[i] - v) <= (nums[i-1] - k):
                    if k in dp[i-1]:
                        cumulative += dp[i-1][k]
                        cumulative %= MOD
                    k += 1
                dp[i][v] = cumulative

        return sum(dp[n-1].values()) % MOD
