const int MOD = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int pre[1001], tmp[1001];
        memset(pre, 0, sizeof(pre));
        for (int i = 0; i <= nums[0]; i++) {
            pre[i] = 1;
        }
        for (int i = 1; i <= 1000; i++) {
            pre[i] = (pre[i] + pre[i - 1]) % MOD;
        }
        for (int i = 1; i < n; i++) {
            memset(tmp, 0, sizeof(tmp));
            for (int j = 0; j <= nums[i]; j++) {
                int t = nums[i - 1] - nums[i] + j;
                if (t >= 0) {
                    tmp[j] = pre[min(t, j)];
                }
            }
            pre[0] = tmp[0];
            for (int j = 1; j <= 1000; j++) {
                pre[j] = (pre[j - 1] + tmp[j]) % MOD;
            }
        }
        return pre[1000];
    }
};
