class Solution {
public:
#define ll long long
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();
        ll m = 1000000007;
        vector<vector<ll> > dp(n+1, vector<ll>(1002,0));
        for(int i=0;i<n;++i){
            int curr = nums[i];
            if(!i){
                for(int j=0;j<=curr;++j){
                    dp[i][j]=1;

                }
                for(int j=1;j<=1000;++j){
                    dp[i][j]+=dp[i][j-1];
                }
            }
            else{

                for(int curr_ele =0;curr_ele<=curr;++curr_ele){
                    // max value of prev_ele ?
                    int low =0, high=curr_ele, ret = -1;
                    while(low<=high){
                        int prev_ele =(low+high)/2;
                        if(prev_ele > nums[i-1]){
                            high=prev_ele-1;continue;
                        }
                        if(nums[i-1] - prev_ele >= nums[i]-curr_ele){
                            ret = prev_ele;
                            low=prev_ele+1;
                        }
                        else{
                            high=prev_ele-1;
                        }

                    }
                    if(ret ==-1){
                        dp[i][curr_ele] =0;
                    }
                    else{
                        dp[i][curr_ele] = dp[i-1][ret];
                    }


                }
                for(int j=1;j<=nums[i];++j){
                    dp[i][j]+=dp[i][j-1];
                    dp[i][j]%=m;
                }
            }
        }
        return dp[n-1][nums[n-1]];

    }
};
