class Solution {
public:
    int mod = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<long long> dp(4002,0);
        for (int i=0;i<=nums[0];i++)dp[i]++;
        for (int j=1;j<n;j++){
            vector<long long> temp(4002,0);
            for (int i=0;i<=nums[j-1];i++){
                if (dp[i]==0)continue;
                long long l = i;
                long long r = nums[j-1] - i;
                long long b = nums[j] - r;
                b = max(b,l);
                if (b>nums[j])continue;
                temp[b] = (temp[b] + dp[i] + mod)%mod;
                temp[nums[j]+1] = (temp[nums[j]+1] - dp[i] + mod)%mod;
            }
            for (int i=1;i<4002;i++){
                temp[i] = (temp[i] + temp[i-1] + mod)%mod;
            }
            dp = temp;
        }
        long long ans = 0;
        for (int i=0;i<4002;i++)ans = (ans + dp[i] + mod)%mod;
        return ans;
    }
};
