class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        n, mx = len(nums), max(nums)
        dp = [[0] * (mx + 1) for _ in range(n)]
        dp[0][0] = 1

        for j in range(1, nums[0] + 1):
            dp[0][j] = 1 + dp[0][j - 1]
        for i in range(1, n):
            if nums[i - 1] - nums[i] >= 0:
                dp[i][0] = dp[i - 1][0]
            for j in range(1, nums[i] + 1):
                t = min(j, nums[i - 1] - nums[i] + j, nums[i - 1])
                if t >= 0:
                    dp[i][j] = (dp[i - 1][t] + dp[i][j - 1]) % MOD
                else:
                    dp[i][j] = dp[i][j - 1]
        return dp[-1][nums[-1]] % MOD
