class Solution {
    public int countOfPairs(int[] nums) {
       final int MOD = 1_000_000_007;

        long[] dp = new long[1001];
        for (int i = 0; i < 1001; i++) dp[i] = i > nums[0]? dp[i-1]: i + 1;

        for (int i = 1; i < nums.length; i++) {
            long[] nxt = new long[1001];
            for (int a1 = 0; a1 <= nums[i]; a1++) {
                int a2 = nums[i] - a1;
                int bound = Math.min(a1, nums[i-1] - nums[i] + a1);
                if (bound >= 0) nxt[a1] = dp[bound];
            }
            dp = new long[1001];
            for (int k = 0; k < 1001; k++) dp[k] = (nxt[k] + (k > 0? dp[k-1]: 0)) % MOD;
        }

        return (int)dp[1000];
    }
}
