#include<bits/stdc++.h>
using namespace std;

#define ff              first
#define ss              second
#define pb              push_back
#define MOD             1000000007
#define inf             1e18

typedef long long int lli;
const lli K = 1e3+5;
class Solution {
public:
	void Out(vector<lli>& dp){
		for(auto& it : dp){
			cout << it << " ";
		}
		cout << endl;
	}
	lli Add(lli a, lli b){
		return ((a % MOD) + (b % MOD)) % MOD;
	}
    int countOfPairs(vector<int>& nums) {
  		lli ret = 0;
  		vector<lli> dp(K);

  		for(int i = 0; i <= nums[0]; i++) dp[i] = i + 1;

  		int n = nums.size();
		// Out(dp);
  		for(int i = 1; i < n; i++){
  			vector<lli> n_dp(K);
  			for(int first_array_num = 0; first_array_num <= nums[i]; first_array_num++){
  				int other_array_num = nums[i] - first_array_num;
  				int prev_first_array_num = -1;
  				int low = 0, high = first_array_num;

  				while(low <= high){
  					int mid = low + (high - low) / 2;
  					int prev_other_array_num = nums[i - 1] - mid;
  					if(prev_other_array_num >= other_array_num){
  						prev_first_array_num = mid;
  						low = mid + 1;
  					}else{
  						high = mid - 1;
  					}
  				}

  				if(prev_first_array_num != -1){
  					n_dp[first_array_num] = Add(dp[prev_first_array_num], n_dp[first_array_num]);
  				}
  			}
  			for(int j = 1; j < K; j++){
  				n_dp[j] = Add(n_dp[j - 1], n_dp[j]);
  			}
  			swap(dp, n_dp);
  			// Out(dp);
  		}



  		return dp[nums.back()];      
    }
};


