class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function countOfPairs($nums) {
        $mod = 1000000007;
        $ans = 0;
        $count = count($nums);
        $mem = [];
        $pSum = [];
        $sum = 0;
        for ($num = 0; $num < $nums[0] + 1; ++ $num) {
            $mem[0][$num] = 1;
            $sum += 1;
            $pSum[0][$num] = $sum;
        }
        for ($index = 1; $index < $count; ++ $index) {
            $sumP = 0;
            for ($num = 0; $num < $nums[$index] + 1; ++ $num) {
                $t = $nums[$index - 1] - $nums[$index] + $num;
                if ($t < 0) {
                    $t = -1;
                } else if ($t > $num) {
                    $t = $num;
                }
                $mem[$index][$num] = $pSum[$index - 1][$t] ?? 0;
                $sumP += $pSum[$index - 1][$t] ?? 0;
                $sumP %= $mod;
                $pSum[$index][$num] = $sumP;
            }
        }

        $ans = 0;
        foreach ($mem[$count - 1] as $num => $cnt) {
            $ans += $cnt;
            $ans %= $mod;
        }

        return $ans;
    }
}
