class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1000000007;

        long long dp[n][1001];
        long long pre[n][1001];

        for(int i=0;i<n;i++){
            for(int j=0;j<=1000;j++){
                dp[i][j]=0;
                pre[i][j]=0;
            }
        }

        for(int j=0;j<=nums[0];j++){
            dp[0][j] = 1;
        }
        for(int j=0;j<=1000;j++){
            pre[0][j] = (j?pre[0][j-1]:0) + dp[0][j];
        }

        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                int l = nums[i-1]-nums[i]+j;
                l = min(l,j);
                if(l<0)continue;
                dp[i][j] = pre[i-1][l];
            }

            for(int j=0;j<=1000;j++){
                pre[i][j] = (j?pre[i][j-1]:0) + dp[i][j];
                pre[i][j]%=mod;
            }
        }

        // for(int i=0;i<n;i++){
        //     for(int j=0;j<=nums[i];j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<"\n";
        // }

        long long ans = pre[n-1][1000];

        return ans;

    }
};
