class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int kMod = 1e9 + 7;
        auto modAdd = [](int a, int b) { return (a+b) % kMod; };
        constexpr int kNumMax = 1000;
        vector<int> dp(kNumMax+1); // arr1 size count
        dp[0] = 1;
        for (int i = 1; i < dp.size(); ++i) {
            dp[i] = dp[i-1] + (i <= nums[0]);
        }

        for (int i = 1; i < nums.size(); ++i) {
            vector<int> dp2(kNumMax+1);
            int num = nums[i];
            for (int arr1Num = 0; arr1Num < dp.size(); ++arr1Num) {
                if (arr1Num > 0) dp2[arr1Num] = dp2[arr1Num-1];
                if (arr1Num > num) continue;
                int arr2Num = num - arr1Num;

                int maxPreArr1Num = min(arr1Num, nums[i-1] - arr2Num);
                if (maxPreArr1Num < 0) continue;
                dp2[arr1Num] = modAdd(dp2[arr1Num], dp[maxPreArr1Num]);
            }
            swap(dp, dp2);
        }
        return dp.back();
    }
};
