class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        V = max(nums)
        dp = [0 for _ in range(V+1)]
        for x in range(nums[0] + 1):
            dp[x] += 1

        for i in range(1, n):
            nd = [0 for _ in range(V+1)]
            p = [0]
            for x in dp:
                p.append(p[-1] + x)
            for a in range(nums[i] + 1):
                a, b = a, nums[i] - a
                r = min(a, a - (nums[i] - nums[i-1]))
                if r < 0:
                    nd[a] = 0
                else:
                    nd[a] = p[r+1]
                nd[a] %= mod
            dp = nd
        return sum(dp) % mod
