class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int md=1e9+7;

        int dp[n+1][1001];memset(dp,0,sizeof(dp));

        for(int i=0;i<=nums[0];i++){dp[0][i]=1;if(i)dp[0][i]+=dp[0][i-1];}


        for(int i=1;i<n;i++){


            for(int j=nums[i];j>=0;j--){

                int mn = min(j,nums[i-1]);
                if(mn < j)continue;
                int mx = max(j,nums[i-1]);
                mn = nums[i-1]-mn;
                mx = nums[i-1]-mx;

                mn=min(mn,nums[i]-j);
                dp[i][nums[i]-j] = dp[i-1][mn]%md;
            }


            for(int j=1;j<=nums[i];j++){
                dp[i][j] = (dp[i][j-1]+dp[i][j])%md;
            }

        }
        int ans=dp[n-1][nums[n-1]];
        return ans;
    }
};
