class Solution {
public:
    const int mod = 1e9+7;
    #define ll long long

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int> v = nums;
        vector<vector<ll>> dp(n, vector<ll> (1001, 0));
        int lt = 0;
        ll ans = 0;
        for(int i = 1; i < n; i++)
        {
            if(v[i] > v[i-1]+lt)
            {
                lt = v[i] - v[i-1];
                v[i] = v[i-1];
            }
            else if(v[i] - lt < 0)
            {
                return 0;
            }
            else
            {
                v[i] -= lt;
            }
        }
        for(int j = 0; j <= v[0]; j++)
        {
            dp[0][j] = j+1;
        }
        for(int i = 1; i < n; i++)
        {
            dp[i][0] = 1;
        }
        for(int i = 1; i < n; i++)
        {
            for(int j = 1; j <= v[i]; j++)
            {
                dp[i][j] = (dp[i][j] + dp[i-1][j])%mod;
                dp[i][j] = (dp[i][j] + dp[i][j-1])%mod;
            }
        }
        // for(int i = 0; i < n; i++)
        // {
        //     for(int j = 0; j <= v[i]; j++)
        //     {
        //         cout << dp[i][j] << " ";
        //     }
        //     cout << "\n";
        // }
        // cout << "\n";
        // for(int i = 0; i <= v[n-1]; i++)
        // {
        //     ans = (ans+dp[n-1][i])%mod;
        // }
        return dp[n-1][v[n-1]];
    }
};
