class Solution {
public:
    long long dp[2001][1001];
    long long mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp[0][0] = 1;
        for(int k=0;k<n;k++) {
            for(int i=0;i<=nums[k];i++) {
                int x = i, y = nums[k] - i;
                int c;
                if (k == 0) c = 1000;
                else c = nums[k - 1];
                if (x > 0) {
                    dp[k + 1][x] = dp[k + 1][x - 1];
                }
                int d = c - x;
                if (d >= y) {
                    dp[k + 1][x] += dp[k][x];
                    dp[k + 1][x] %= mod;
                }
                int e = c - y;
                if (e <= x && e >= 0 && e != x) {
                    dp[k + 1][x] += dp[k][e];
                    dp[k + 1][x] %= mod;
                }
            }
        }
        long long ans = 0;
        for(int k=0;k<=1000;k++) {
            int i = nums[n - 1] - k;
            if (i >= 0) {
                ans += dp[n][k];
                ans %= mod;
            }
        }
        return ans;
    }
};
