        // return ans;
        int mn=1e9;
        for (int i=n-1;i>=0;i--){
            if (i==n-1){
                mn=min(mn,a[i]);
                for (int j=0;j<=mn;j++){
                    dp[i][j]=1;
                   // cout<<i<<" "<<j<<" "<<dp[i][j]<<"\n";
                }
            }
            else{
                int sf=mn;
                mn=min(mn,a[i]);
                for (int j=0;j<=mn;j++){
                    int l=max(j,j-(a[i]-a[i+1]));
                    int r=sf;
                    if (l>r) continue;
                    long long f=dp[i+1][r]-(l-1>=0?dp[i+1][l-1]:0);
                    f%=mod;
                    f+=mod;
                    f%=mod;
                    dp[i][j]=f;
                   // cout<<i<<" "<<j<<" "<<dp[i][j]<<"\n";
                }
            }
            for (int j=1;j<=1000;j++){
                dp[i][j]+=dp[i][j-1];
                dp[i][j]%=mod;
            }
        }
        int ans=dp[0][1000];
        return ans;
    }
