class Solution {
    using ll = long long;
    using pii = pair<int, int>;
    using pll = pair<ll, ll>;
    // const ll mod = 998244353;
    const ll mod = 1'000'000'007;
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int vmax = 1000;
        vector<vector<ll>> dp(n, vector<ll>(vmax + 1, 0));
        for(int i = 0; i <= nums.front(); i++){
            dp[0][i] = 1;
        }
        for(int i = 1; i < n; i++){
            int x = nums[i];
            int px = nums[i-1];
            ll pfs = 0;
            int last = -1;
            for(int v = 0; v <= x; v++){
                int pv = min(px - x + v, v);
                if(v == 0){
                    for(int j = 0; j <= pv; j++) {
                        pfs = (pfs + dp[i-1][j]) % mod;
                    }
                }
                else{
                     if(pv >= 0 && pv <= vmax && pv != last) pfs = (pfs + dp[i-1][pv]) % mod;
                }
                last = pv;
                pfs %= mod;
                dp[i][v] = (dp[i][v] + pfs) % mod;
            }
        }
        ll res = accumulate(dp.back().begin(), dp.back().end(), 0LL);
        res %= mod;
        return res;
    }
};
